import axios from 'axios'
import React, { useState, useEffect} from 'react'

 const ProductDetails = ({ match }) => {
//   console.log(match)

  const [product, setProduct] = useState(null)
  const [ loading, setLoading] = useState(true);

  const getProduct = async () => {
      
      const res =await axios.get(`http://localhost:9999/api/products/${match.params.id}`)
      console.log(res.data)
      setProduct(res.data)
      setLoading(false)
  }


  useEffect(() => {
      getProduct()
  }, [])

    if(!loading) {

    
        return (
            <div className="product-detials  p-2 d-flex justify-content-between align-items-center " >

                    <img
                    src={product.image}
                    className="card-img-top"
                    alt="..."
                    />
                       
                        <h2>{ product.name }</h2>
                        <div className="product-desc  d-flex align-items-center col-lg-6">
                        <p> { product.short } </p>
                        <p> { product.desc } </p>
                </div>
            
            </div>
        )
    } else {
        return (
            <div className="product-details">
                <h3>Loading...</h3>
            </div>
        )
    } 

}

export default ProductDetails
