import actiontypes from '../actiontypes';

const initState =  {
    product: null,
    loading: true
}

const productCatalogReducer = ( state = initState, action) => {
    switch(action.type) {
      case actiontypes().productCatalog.set:
          state = action.payload
              return state 

        default:
            return state 
    }
 

}


export default productCatalogReducer;
